@lru_cache(maxsize=None)
def f(i, arr):
    if i == m:
        return sum(arr)
    divisible = []
    not_divisible = []
    for x in arr:
        if x % q[i] == 0:
            divisible.append(x)
        else:
            not_divisible.append(x)
    divisible = tuple(divisible)
    not_divisible = tuple(not_divisible)
    if i % 2 == 0:
        return min(f(i + 1, divisible), f(i + 1, not_divisible))
    else:
        return max(f(i + 1, divisible), f(i + 1, not_divisible))
