        if ispref == True:
            hi = ord(strings[i + 1][pos]) - ord('a') + 1
        else:
            hi = 26
        if pos == len(strings[i]):
            ans = 'No'
            for c in range(hi):
                if used[c] < have[c]:
                    ans = 'Yes'
                    break
            break
        lo = ord(strings[i][pos]) - ord('a') + 1
        ans = 'No'
        for c in range(lo, hi):
            if used[c] < have[c]:
                ans = 'Yes'
                break
        if ans == 'Yes':
            break
        if used[lo - 1] < have[lo - 1]:
            used[lo - 1] += 1
            if ispref == True and strings[i][pos] != strings[i + 1][pos]:
                ispref = False
            ans = 'Yes'
            continue
        ans = 'No'
        break
    print(ans)

