import sys
input = sys.stdin.readline
for _ in range(int(input())):
    n = int(input())
    a = list(input().strip())
    b = list(input().strip())
    if a.count('0') != b.count('0'):
        print('NO')
        continue
    if a == b:
        print('YES')
        continue
    flag = 0
    flag1 = 0
    for i in range(1, n):
        if a[i] == a[i - 1]:
            flag = 1
        if b[i] == b[i - 1]:
            flag1 = 1
    if flag and flag1:
        print('YES')
    else:
        print('NO')

