import math

def getGCD(arr):
    lenArr = len(arr)
    if lenArr == 0:
        return 0
    if lenArr == 1:
        return arr[0]
    ans = arr[0]
    for i in arr[1:lenArr]:
        ans = math.gcd(ans, i)
        if ans == 1:
            return ans
    return ans
