import sys
input = sys.stdin.readline

def il():
    return list(map(int, input().split()))

def ii():
    return int(input())

def imi():
    return map(int, input().split())

def ist():
    return input()
p = 10 ** 9 + 7

def power(x, y, m):
    if y == 0:
        return 1
    if x == 1:
        return 1
    a = power(x, y // 2, m) % m
    a = a * a % m
    if y % 2 == 0:
        return a
    else:
        return x * a % m

def mmi(a, m):
    return power(a, m - 2, m)
mi = [0] * (10 ** 5 + 7)
for i in range(1, 10 ** 5 + 5):
    mi[i] = mmi(i, p)

def solve(n, m):
    a = 1
    s = 1
    for i in range(1, n + 2):
        a = a * (m + 2 - i) * mi[i] % p
        s = (s + a) % p
    return (s, a)
t = ii()
for _ in range(t):
    (n, m) = imi()
    if n > m:
        print((power(2, m + 2, p) + p - 1) % p)
    else:
        (s, x) = solve(n, m)
        print((2 * s - 1 - x + p) % p)

