for _ in range(int(input())):
    (n, k) = map(int, input().split())
    (l, bits) = (list(map(int, input().split())), [[] for _ in range(k)])
    for i in range(k):
        for j in range(n):
            if pow(2, i) & l[j]:
                bits[i].append(j)
    ans = 1
    for i in range(k):
        temp = n - len(bits[i]) + 1
        for j in range(len(bits[i])):
            temp = (temp + (n - bits[i][j]) - (len(bits[i]) - j - 1)) % 998244353
        ans = ans * temp % 998244353
    print(ans)

