T = int(input())

def gcd(u, v):
    if v == 0:
        return u
    return gcd(v, u % v)
for t in range(T):
    N = int(input())
    A = [int(h) for h in input().split()]
    g = 0
    skip1 = []
    skip2 = []
    for a in A:
        for i in range(len(skip2)):
            (g2, skips) = skip2[i]
            skip2[i] = (gcd(g2, a), skips)
        for i in range(len(skip1)):
            (g1, skip) = skip1[i]
            skip2.append((g1, (skip, a)))
        skip2.sort(reverse=True)
        skip2_ = []
        for i in range(len(skip2)):
            admissible = True
            for j in range(i - 1, -1, -1):
                if skip2[j][0] % skip2[i][0] == 0:
                    admissible = False
                    break
            if admissible:
                skip2_.append(skip2[i])
        skip2 = skip2_
        for i in range(len(skip1)):
            (g1, skip) = skip1[i]
            skip1[i] = (gcd(g1, a), skip)
        skip1.append((g, a))
        skip1.sort(reverse=True)
        skip1_ = []
        for i in range(len(skip1)):
            admissible = True
            for j in range(i - 1, -1, -1):
                if skip1[j][0] % skip1[i][0] == 0:
                    admissible = False
                    break
            if admissible:
                skip1_.append(skip1[i])
        skip1 = skip1_
        g = gcd(g, a)
    if len(skip1) > 0:
        g = max(g, skip1[0][0])
    if len(skip2) > 0:
        g = max(g, gcd(skip2[0][0], skip2[0][1][0] * skip2[0][1][1]))
    print(g)

