    if coin > val:
        memo[coin] = coin
        return coin
    else:
        c4 = coins(math.floor(coin / 4))
        c3 = coins(math.floor(coin / 3))
        c2 = coins(math.floor(coin / 2))
        memo[coin] = c4 + c3 + c2
        return c4 + c3 + c2
