def count_punished_pairs(s):
    n = len(s)
    t = [''] * n
    for i in range(n):
        if s[i] == '*':
            t[i] = '*'
        elif s[i] == '<' and i > 0:
            t[i] = '>'
        elif s[i] == '>' and i < n - 1:
            t[i] = '<'
    punished_pairs = 0
    for i in range(n - 1):
        if t[i] == '>' and t[i + 1] == '<':
            punished_pairs += 1
    return punished_pairs
