def can_be_doof_stopped(values: typing.List[int]) -> bool:
    _lcm = 1
    for value in values:
        _lcm = lcm(_lcm, value)
    return _lcm % 2 == 1

def lcm(a: int, b: int) -> int:
    return a * b // gcd(a, b)

def gcd(a: int, b: int) -> int:
    while b != 0:
        (a, b) = (b, a % b)
    return a
