def check(a, b):
    a = str(a)
    b = str(b)
    l1 = [int(x) for x in a]
    l2 = [int(x) for x in b]
    if sum(l1) == sum(l2):
        return 2
    elif sum(l1) > sum(l2):
        return 0
    else:
        return 1
