def remainderA(a):
    su = 0
    while a != 0:
        rem = a % 10
        su += rem
        a = a // 10
    return su

def remainderB(b):
    suB = 0
    while b != 0:
        rem = b % 10
        suB += rem
        b = b // 10
    return suB
