    for i in range(r):
        (a, b) = map(int, input().split())
        a1 = remainderA(a)
        b1 = remainderB(b)
        if a1 > b1:
            c += 1
        elif a1 < b1:
            m += 1
        elif a1 == b1:
            c += 1
