class Solution:

    def solve(self, array, length):
        array = sorted(array, reverse=True)
        ans = array[0] - array[1]
        prev = array[1]
        for value in array[2:]:
            diff = prev - value
            if diff < ans:
                ans = diff
            prev = value
        return ans

class Driver:

    def __init__(self):
        self._solver = Solution()

    def drive(self):
        test_cases = int(input().strip())
        for _ in range(test_cases):
            length = int(input().strip())
            array = list(map(int, input().strip().split(' ')))
            ans = self._solver.solve(array, length)
            print(ans)
