    n = len(s)
    if n % 2 == 0:
        s1 = s[:n // 2]
        s2 = s[n // 2:]
    else:
        s1 = s[:n // 2]
        s2 = s[n // 2 + 1:]
    freq1 = {}
    freq2 = {}
    for c in s1:
        freq1[c] = freq1.get(c, 0) + 1
    for c in s2:
        freq2[c] = freq2.get(c, 0) + 1
    return freq1 == freq2
