def leftshift(s):
    n = len(s)
    lis = list(s)
    for i in range(0, n - 1):
        lis[i] = lis[i + 1]
    lis[n - 1] = s[0]
    return ''.join(lis)

def rightshift(s):
    n = len(s)
    lis = list(s)
    for i in range(n - 1, 0, -1):
        lis[i] = lis[i - 1]
    lis[0] = s[n - 1]
    return ''.join(lis)
