def infect(lst, k):
    h = lst.index(k)
    g = [lst[h]]
    if h > 0:
        for i in range(lst.index(k) - 1, -1, -1):
            if lst[h] - lst[i] <= 2 and lst[h] - lst[i] >= 0:
                g = [lst[h - 1]] + g
                h = h - 1
            else:
                break
    h = lst.index(k)
    if h < len(lst) - 1:
        for i in range(h + 1, len(lst)):
            if lst[i] - lst[h] <= 2 and lst[i] - lst[h] >= 0:
                g = g + [lst[h + 1]]
                h = h + 1
            else:
                break
    return g

