def average_after_manipulations(n, k, measurements):
    measurements.sort()
    return sum(measurements[k:n - k]) / (n - 2 * k)
t = int(input())
for i in range(t):
    (n, k) = map(int, input().split())
    measurements = list(map(int, input().split()))
    print('{:.6f}'.format(average_after_manipulations(n, k, measurements)))

