def decimalToBinary(n, l):
    a = bin(n).replace('0b', '')
    while len(a) < l:
        a = '0' + a
    return a
for _ in range(int(input())):
    n = int(input())
    a = sorted([int(x) for x in input().split()])
    possible_time = set()
    for i in range(2 ** n):
        binary_i = decimalToBinary(i, n)
        current_time = [0, 0]
        for j in range(n):
            current_time[int(binary_i[j])] += a[j]
        possible_time.add(max(current_time))
    print(min(possible_time))

