def solve(n, lst):
    if n <= 2:
        print(max(lst))
    else:
        lst.sort()
        res = 0
        while True:
            if len(lst) == 2:
                res += max(lst)
                break
            res += lst[-2]
            lst[-1] -= lst[-2]
            lst.remove(lst[-2])
            lst.sort()
        print(res)
