class Line:

    def __init__(self, start, ttime, nextDep):
        self.start = start
        self.ttime = ttime
        self.nextDep = nextDep

    def travel(self, t):
        tDep = self.start
        while True:
            if t <= tDep:
                return tDep + self.ttime
            tDep += self.nextDep
