from collections import defaultdict

def river(x, y, m, c):
    river_y = m * x + c
    return 1 if river_y > y else 0

def solve(n, m, c, villagers):
    sorted_villagers = defaultdict(int)
    for v in villagers:
        sorted_villagers[river(v[0], v[1], m, c)] += v[2]
    return max(sorted_villagers[0], sorted_villagers[1])

