def eq(a, b, c):
    return abs(a - b) + abs(b - c) + abs(c - a)
t = int(input())
for _ in range(t):
    n = int(input())
    arr = list(map(int, input().split()))
    arr.sort()
    a = arr[0]
    c = arr[-1]
    m = 0
    for i in arr[1:-1]:
        m = max(m, eq(a, i, c))
    print(m)

def cal(x, y, z):
    print(abs(x - y) + abs(y - z) + abs(x - z))
for _ in range(int(input())):
    n = int(input())
    a = list(map(int, input().split()))
    a.sort()
    if len(a) == 3:
        cal(a[0], a[1], a[2])
    else:
        a.sort()
        cal(a[-1], a[0], a[1])

