def yn(b, caps=False, y='Yes', n='No'):
    if b:
        returning = y
    else:
        returning = n
    if caps == 2:
        return returning.lower()
    elif caps == 3:
        return ''.join([returning[i].lower() if i % 2 == 0 else returning[i].upper() for i in range(len(returning))])
    elif caps:
        return returning.upper()
    return returning

def prob(l, n):
    return l.count(n) / len(l)

def isSubseq(s1, s2):
    (n, m) = (len(s1), len(s2))
    (i, j) = (0, 0)
    while i < n and j < m:
        if s1[i] == s2[j]:
            i += 1
        j += 1
    return i == n

def isPrime(n):
    if n > 1:
        for i in range(2, int(n ** 0.5) + 1):
            if not n % i:
                return False
        else:
            return True
    return True

