def prodLst(l1, nl):
    if isinstance(nl, int):
        return [i * nl for i in l1]
    else:
        return [i * j for (i, j) in zip(l1, nl)]

def divLst(l1, nl, mxMn=False):
    if isinstance(nl, int):
        return [i // nl for i in l1]
    elif mxMn:
        return [max(i, j) // min(i, j) for (i, j) in zip(l1, nl)]
    else:
        return [i // j for (i, j) in zip(l1, nl)]

def modLst(l, n):
