def diffLst(l1, nl, isAbs=False):
    if isinstance(nl, int):
        returning = [i - nl for i in l1]
    else:
        returning = [i - j for (i, j) in zip(l1, nl)]
    if isAbs:
        return absLst(returning)
    else:
        return returning

