def a_pwr_n(a, n, mod):
    if n == 1:
        return a % mod
    if n % 2 == 0:
        val = a_pwr_n(a, n // 2, mod)
        return val % mod * val % mod
    else:
        return a % mod * a_pwr_n(a, n - 1, mod) % mod

def rg(*ag):
    if len(ag) == 3:
        return range(ag[0], ag[1], ag[2])
    elif len(ag) == 2:
        return range(ag[0], ag[1])
    elif len(ag) == 1:
        return range(ag[0])
    else:
        raise 'invalid input'

