def multiply(aaa, bbb):
    aaa.reverse()
    bbb.reverse()
    ccc = [0 for k in range(0, len(aaa) + len(bbb))]
    for i in range(0, len(aaa)):
        for j in range(0, len(bbb)):
            ccc[i + j] += aaa[i] * bbb[j]
    for m in range(0, len(ccc)):
        if ccc[m] > 9:
            ccc[m + 1] += int(ccc[m] / 10)
            ccc[m] = ccc[m] % 10
    ccc.reverse()
    return ccc

def add(aaa, bbb):
    aaa.reverse()
    bbb.reverse()
    (a, b) = (len(aaa), len(bbb))
    c = max(len(aaa), len(bbb)) + 1
    ccc = [0 for k in range(0, c)]
    carry = 0
    for i in range(c):
        if i >= a:
            first = 0
        else:
            first = aaa[i]
        if i >= b:
            second = 0
        else:
            second = bbb[i]
        ccc[i] = first + second + carry
        carry = 0
        if ccc[i] > 9:
            carry = 1
            ccc[i] = ccc[i] - 10
    ccc.reverse()
    return ccc

