import sys
input = sys.stdin.readline

def rii():
    return range(int(input().strip()))

def ii():
    return int(input().strip())

def mii():
    return map(int, input().strip().split(' '))

def lmii():
    return list(map(int, input().strip().split(' ')))

def si():
    return str(input().strip())
import re
regex = '(0{3,})'

def solve(*args):
    (n, s) = args
    added = 0
    if n == 1 and s == '0':
        return 1
    if s.startswith('00'):
        s = '1' + s[1:]
        added += 1
    if s.endswith('00'):
        s = s[:-1] + '1'
        added += 1
    for r in re.findall(regex, s):
        added += (len(r) - 1) // 2
    return added

def do_codechef():
    for t in rii():
        n = ii()
        s = si()
        print(solve(n, s))
do_codechef()
exit()

