def primeFactor(num):
    factor = []
    while num % 2 == 0:
        factor.append(2)
        num = num >> 1
    for j in range(3, int(sqrt(num)) + 1, 2):
        while num % j == 0:
            factor.append(j)
            num = num // j
    if num > 2:
        factor.append(num)
    return factor
