import sys
input = sys.stdin.readline

def rii():
    return range(int(input().strip()))

def ii():
    return int(input().strip())

def mii():
    return map(int, input().strip().split(' '))

def lmii():
    return list(map(int, input().strip().split(' ')))

def si():
    return str(input().strip())
conversion_cost = {('G', 'R'): 3, ('R', 'G'): 5, ('G', 'G'): 0, ('R', 'R'): 0}
chars = ['G', 'R']

def make_alternating(ch, l):
    return ''.join([chars[(x + ch) % 2] for x in range(l)])

def solve(*args):
    (n, m, grid) = args
    (c1, c2) = (0, 0)
    for i in range(n):
        c1 += sum([conversion_cost[x] for x in zip(grid[i], make_alternating(i % 2, m))])
        c2 += sum([conversion_cost[x] for x in zip(grid[i], make_alternating((i + 1) % 2, m))])
    return min(c1, c2)

def do_codechef():
    for t in rii():
        (n, m) = mii()
        grid = list()
        for _ in range(n):
            grid.append(si())
        print(solve(n, m, grid))
do_codechef()
exit()

