import math

def find_arc_length(r, a):
    return r / math.cos(a)

def find_centroid(s):
    return math.sqrt(s ** 2 - (s / 2) ** 2) * 2 / 3
t = int(input())
for i in range(t):
    line = input()
    line = line.split()
    s = int(line[0])
    v = int(line[1])
    print(find_arc_length(find_centroid(s), math.pi / 6) / v)
