def busqueda_binaria(nums, x, a_insertar):
    izq = 0
    der = len(nums) - 1
    while izq <= der:
        medio = int((izq + der) / 2)
        if nums[medio] == x:
            return medio
        elif nums[medio] > x:
            der = medio - 1
        else:
            izq = medio + 1
    if a_insertar:
        nums.insert(medio + 1, x)
    return -1

