def nextOccurrence(strr, n, start, ch):
    for i in range(start, n):
        if strr[i] == ch:
            return i
    return -1

def countSubStr(strr, n, ch):
    cnt = 0
    j = nextOccurrence(strr, n, 0, ch)
    for i in range(n):
        while j != -1 and j < i:
            j = nextOccurrence(strr, n, j + 1, ch)
        if j == -1:
            break
        cnt += n - j
    return cnt
