def nc2(n):
    if n < 2:
        return 0
    return n * (n - 1) // 2

def solve2(A):
    ones = twos = zeros = 0
    for i in A:
        if i == 1:
            ones += 1
        elif i == 2:
            twos += 1
        elif i == 0:
            zeros += 1
    n = len(A)
    tot = nc2(len(A))
    onepairs = nc2(ones)
    onecombos = (n - ones) * ones
    zeropairs = nc2(zeros)
    zerocombos = (n - zeros - ones) * zeros
    twopairs = nc2(twos)
    return tot - (onepairs + onecombos + twopairs + zeropairs + zerocombos)
