def val(a, b):
    return (2 * a + 1 if a & 1 else 2 * a) + (2 * b - 1 if b & 1 else 2 * b)

def solve(x, y):
    x = abs(x)
    y = abs(y)
    z = min(x, y)
    return 2 * z + val(x - z, y - z)
