class BTree:

    def __init__(self, root, left=None, right=None):
        self.root = root
        self.left = left
        self.right = right

    def correct(self, N):
        for num in N:
            if num > self.root:
                if not self.updateRight(num):
                    return False
            elif num < self.root:
                if not self.updateLeft(num):
                    return False
        return True

    def updateLeft(self, num):
        if self.left:
            if num > self.left:
                self.left = num
                return True
        else:
            self.left = num
            return True
        return False

    def updateRight(self, num):
        if self.right:
            if num < self.right:
                self.right = num
                return True
        else:
            self.right = num
            return True
        return False
