    right_limit = 100000000000
    for i in range(N - 1):
        if i == 0:
            if arr[i] > R:
                right_limit = arr[i]
            elif arr[i] < R:
                left_limit = arr[i]
        elif arr[i] > left_limit and arr[i] < right_limit:
            if arr[i] > R:
                right_limit = arr[i]
            elif arr[i] < R:
                left_limit = arr[i]
        else:
            return False
    return True
