import fileinput

def solveSingle(n, a, b, d):
    x = a + b
    if d == n:
        return f'{x} {2 ** d}'
    elif d % 2 == 1:
        return solveSingle(n, 2 * a, x, d + 1)
    else:
        return solveSingle(n, x, 2 * b, d + 1)
with fileinput.input() as f:
    line = f.readline().strip().split(' ')
    t = int(line[0])
    if t != len(line) - 1:
        raise ValueError(f'unexpected lengths {t} {len(line) - 1}')
    print(' '.join((solveSingle(int(n), 0, 1, 1) for n in line[1:])))

