                max_pats += x
            elif counts['O'] > 0:
                max_pats += counts['O']
                counts['O'] = 0
            else:
                continue
        elif counts['E'] >= x:
            counts['E'] -= x
            max_pats += x
        elif counts['E'] > 0:
            max_pats += counts['E']
            counts['E'] = 0
        else:
