    if start is None:
        start = last = i
    elif i - 1 == last:
        last = i
    elif last - start == 1:
        ans += str(start) + ',' + str(last) + ','
        start = last = i
    elif last == start:
        ans += str(start) + ','
        start = last = i
    else:
        ans += str(start) + '...' + str(last) + ','
        start = last = i
    return (start, last, ans)
