def solution(a, b, k):
    s = sum((x * y for (x, y) in zip(a, b)))
    maxi = s + 0
    for i in range(len(b)):
        if s + k * b[i] > maxi:
            maxi = s + k * b[i]
        if s - k * b[i] > maxi:
            maxi = s - k * b[i]
    return maxi
