def sol1(A, B, k):
    (_, idx) = max(((abs(x), i) for (i, x) in enumerate(B)))
    if B[idx] > 0:
        A[idx] += k
    else:
        A[idx] -= k
    return sum((a * b for (a, b) in zip(A, B)))
