from math import fabs

def getkey(a):
    return int(fabs(a[1]))
t = int(input())
for k in range(t):
    (n, k) = map(int, input().split(' '))
    a = [int(i) for i in input().split(' ')]
    b = [int(i) for i in input().split(' ')]
    g = []
    for i in range(n):
        g.append([a[i], b[i]])
    g.sort(key=getkey, reverse=True)
    if g[0][1] >= 0:
        g[0][0] += k
    else:
        g[0][0] -= k
    ss = 0
    for i in range(n):
        ss += g[i][0] * g[i][1]
    print(ss)
