        mid = low + int((high - low) / 2)
        a = low
        b = mid + 1
        sort(low, mid, L)
        sort(mid + 1, high, L)
        temp = []
        while a <= mid or b <= high:
            if a <= mid and b <= high:
                if L[a] > L[b]:
                    temp.append(L[a])
                    a += 1
                else:
                    temp.append(L[b])
                    b += 1
            elif a <= mid and b > high:
                temp.append(L[a])
                a += 1
            elif a > mid and b <= high:
                temp.append(L[b])
                b += 1
        for i in range(len(temp)):
            L[low + i] = temp[i]

