def io(func=str, n=1):

    def nfp():
        ff = str
        try:
            it = iter(func)

            def nf(val):
                nonlocal ff
                ff = next(it, ff)
                return ff(val)
            return type(func)((nf(va) for va in input().split()))
        except TypeError:
            return func(input())
    if type(n) is list:
        return [_(func, x) for x in n]
    else:
        t = lambda : nfp()
        if n == 1:
            return t()
        elif n == 0:
            return [t() for i in range(int(input()))]
        else:
            return [t() for i in range(n)]

