def solve(n, i):
    l = [i for i in range(i, i + n)]
    z = n * (n + 1) // 2
    sum = 0
    for i in range(n):
        la = []
        for j in range(i, n):
            la.append(l[j])
            if len(la) % 2 != 0:
                sum += la[(len(la) - 1) // 2]
            else:
                sum += (la[(len(la) - 1) // 2] + la[(len(la) - 1) // 2 + 1]) / 2
    k = sum / z
    if n - 1 <= k <= n + 1:
        return l
    else:
        return False
