def solve(x):
    ret = ''
    if x == 1:
        return '1'
    if x % 2 == 1:
        s = x - 1
        ret = [s]
        for i in range(1, x // 2 + 1):
            ret = [s - i] + ret + [s + i]
    else:
        ret = []
        for i in range(x // 2):
            ret = [x - i - 1] + ret + [x + i]
    return ' '.join(map(str, ret))
import sys
