def moveOnce(arr, d, avg):
    total = 0
    done = True
    for i in range(len(arr) - d):
        if arr[i] == avg:
            continue
        elif arr[i] > avg:
            total += arr[i] - avg
            arr[i + d] += arr[i] - avg
            arr[i] = avg
        else:
            moves = avg - arr[i]
            if moves > arr[i + d]:
                moves = arr[i + d]
            total += moves
            arr[i] += moves
            arr[i + d] -= moves
            if arr[i] != avg:
                done = False
    for i in range(len(arr) - d, len(arr)):
        if arr[i] != avg:
            done = False
            break
    return (total, done)

