def longest_substring(s, k):
    n = len(s)
    if k > 1:
        s += s
        n *= 2
    ans = 0
    i = 0
    while i < n:
        x = 0
        while i < n and s[i] == '0':
            (x, i) = (x + 1, i + 1)
        ans = max(ans, x)
        i += 1
    if k == 1 or ans != n:
        return ans
    else:
        return ans // 2 * k
